mapper.update:
update tb_orders
set uid = #{uid,jdbcType=INTEGER},
  createtime = #{createtime,jdbcType=TIMESTAMP},
  totalprice = #{totalprice,jdbcType=DOUBLE}
where oid = #{oid,jdbcType=INTEGER}

mapper.select:
select oid, uid, createtime, totalprice
from tb_orders
where oid = #{oid,jdbcType=INTEGER}
